  medium_text_column MEDIUMTEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  long_text_column LONGTEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  enum_column ENUM('A','B') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  set_column SET('A','B') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  geometry_column GEOMETRY NOT NULL,
